mapper.update:
update ea_notice
set en_hit=en_hit+1
where en_num=#{en_num}


update ea_notice
set en_sub = #{en_sub}, en_cont =#{en_cont},
	en_file = #{en_file}, en_original=#{en_original}
where en_num = #{en_num}

mapper.select:
    	select count(*) from ea_notice
    	
    	
			
			
			
			
				en_admin like #{find_name}
			
			
				en_sub like #{find_name}
			
			
				en_cont like #{find_name}

select * from (select rownum rnum, en_num, en_admin, en_sub, en_cont, en_hit, en_file, en_original, en_date from (select * from ea_notice
en_admin like #{find_name}
en_sub like #{find_name}
en_cont like #{find_name}
order by en_num desc)) where rnum >= ((#{page} - 1) * #{limit} + 1) and rnum <= (#{page} * #{limit}) order by EN_NUM desc
