mapper.select:
select * from ea_member
where em_id = #{em_id}
	and em_state=1

mapper.update:
    update ea_member
    	set em_pass=#{em_pass},
				em_phone=#{em_phone}, em_email=#{em_email},
				em_post=#{em_post}, em_address1=#{em_address1}, em_address2=#{em_address2},
				em_intro=#{em_intro}, em_account=#{em_account}, em_bank=#{em_bank}
		where em_id=#{em_id}
